// src/pages/Menu.js
import React, { useState, useEffect } from 'react';

const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    // Эмуляция загрузки данных с сервера
    const fetchMenuItems = () => {
      setTimeout(() => {
        setMenuItems([
          { id: 1, name: 'Pasta' },
          { id: 2, name: 'Pizza' },
          { id: 3, name: 'Salad' },
        ]);
      }, 1000);
    };

    fetchMenuItems();
  }, []); // Пустой массив зависимостей, чтобы загрузить данные только один раз

  return (
    <div>
      <h1>Our Menu</h1>
      <ul>
        {menuItems.length === 0 ? (
          <li>Loading menu...</li>
        ) : (
          menuItems.map(item => <li key={item.id}>{item.name}</li>)
        )}
      </ul>
    </div>
  );
};

export default Menu;
